// Shared types — single source of truth for all domain types, enums, and DTOs.
// ─── Enums ────────────────────────────────────────────────────────────────────

export type StrategyMode = 'EASY' | 'ADVANCED';
export type AlertType = 'IN_PLAY' | 'PRE_MATCH';
export type RuleValueType = 'IN_PLAY' | 'PRE_MATCH' | 'ODDS';
export type Comparator = 'GTE' | 'LTE' | 'EQ' | 'GT' | 'LT' | 'NEQ';
export type TeamScope =
  | 'HOME'
  | 'AWAY'
  | 'TOTAL'
  | 'EITHER_TEAM'
  | 'EITHER_OPPONENT'
  | 'DIFFERENCE'
  | 'FAVOURITE'
  | 'FAVOURITE_HOME'
  | 'FAVOURITE_AWAY'
  | 'UNDERDOG'
  | 'UNDERDOG_HOME'
  | 'UNDERDOG_AWAY'
  | 'WINNING_TEAM'
  | 'LOSING_TEAM';
export type TriggerResult = 'HIT' | 'MISS';

// ─── Domain rows (mirrors Postgres tables) ───────────────────────────────────

export interface Strategy {
  id: string;
  user_id: string;
  name: string;
  description: string | null;
  mode: StrategyMode;
  alert_type: AlertType;
  desired_outcome: string | null;
  is_active: boolean;
  created_at: string;
}

export interface Rule {
  id: string;
  strategy_id: string;
  value_type: RuleValueType;
  metric: string;
  comparator: Comparator;
  value: number;
  team_scope: TeamScope | null;
  time_filter: Record<string, unknown> | null;
  created_at: string;
}

export interface Trigger {
  id: string;
  strategy_id: string;
  match_id: string;
  triggered_at: string;
  result: TriggerResult | null;
}

export interface Performance {
  strategy_id: string;
  total_triggers: number;
  total_hits: number;
  total_misses: number;
  hit_rate: string; // numeric(5,2) serialised as string
}

// ─── API payloads ─────────────────────────────────────────────────────────────

export interface CreateStrategyPayload {
  name: string;
  description?: string;
  mode?: StrategyMode;
  alert_type: AlertType;
  desired_outcome?: string;
}

export interface PatchStrategyPayload {
  name?: string;
  description?: string | null;
  mode?: StrategyMode;
  alert_type?: AlertType;
  desired_outcome?: string | null;
  is_active?: boolean;
}

export interface CreateRulePayload {
  value_type: RuleValueType;
  metric: string;
  comparator: Comparator;
  value: number;
  team_scope?: TeamScope;
  time_filter?: Record<string, unknown>;
}

