export const METRICS_BY_TYPE: Record<RuleValueType, MetricEntry[]> = {
  IN_PLAY: IN_PLAY_METRICS,
  PRE_MATCH: PRE_MATCH_METRICS,
  ODDS: ODDS_METRICS,
};

// Reverse-lookup: metric key → display label (searches all value-types).
export function metricLabel(key: string): string {
  for (const list of Object.values(METRICS_BY_TYPE)) {
    const hit = list.find((m) => m.key === key);
    if (hit) return hit.label;
  }
  return key; // fallback to raw key
}

// Check if a metric requires team scope selection
export function metricRequiresTeamScope(key: string): boolean {
  for (const list of Object.values(METRICS_BY_TYPE)) {
    const hit = list.find((m) => m.key === key);
    if (hit) return hit.requiresTeamScope;
  }
  return false; // fallback: if metric not found, assume no team scope needed
}
